import React from 'react';
import { useState, useEffect } from 'react';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Typography from '@mui/material/Typography';
import Accordion from '@mui/material/Accordion';
import AccordionSummary from '@mui/material/AccordionSummary';
import AccordionDetails from '@mui/material/AccordionDetails';

export default function ImageCard(props : any) {
    const [imageData, setImageData] = useState(props.data)

    useEffect(() => {
        console.log(imageData)
    })

    return (
        <Card sx={{maxWidth: 300, marginTop: 2}}>
            <CardMedia
            component="img"
            height="300"
            image={imageData['url']}
            />
            <CardContent sx={{paddingX: 0}}>
                <Accordion sx={{width: 300, border: "none", "boxShadow": "0px 0px", "textAlign": "center"}}>
                    <AccordionSummary sx={{paddingX: 1, marginTop: 0}}>
                    <Typography gutterBottom variant="h5" component="div">{imageData['title']}</Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                    <Typography variant="body2" color="text.secondary">{imageData['explanation']}</Typography>
                    </AccordionDetails>
                </Accordion>
            </CardContent>
            {/* <CardActions>
                <Typography>I'm an action bro</Typography>
            </CardActions> */}
        </Card>
    )
}
